import { Component, OnInit } from '@angular/core';
import { ProductsService } from 'src/app/service/products.service';
import { product } from 'src/app/models/product';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit{
  products: product[] = [];
  category
  id: any;
  constructor(private ProSrev: ProductsService, private activRout: ActivatedRoute) { 
  }
  ngOnInit(): void {
    this.getProduct()
  }
  getProduct() {
    this.ProSrev.getallproducts().subscribe((res: any) => {
      this.products = res
      console.log(this.products)
    })
  }

}
<p>home!</p>
<app-select [products]="products"></app-select>
<div class="cardWrap container">
    <div *ngFor="let product of products">
    <app-product [prod]="product"></app-product>
    </div>
</div>
