import { Component } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { ProductsService } from 'src/app/service/products.service';

@Component({
  selector: 'app-edit',
  templateUrl: './edit.component.html',
  styleUrls: ['./edit.component.css']
})
export class EditComponent implements {
  id: any;
  product: any;
  constructor(private actRout: ActivatedRoute,private proServe:ProductsService) { }

  getId() {
    this.id = this.actRout.snapshot.paramMap.get('id');
  }

  getPro(){
    this.proServe.getSigleProduct(this.id).subscribe((res: any) => {
      this.product=res
    })
  }
}
