import { Component, OnInit } from '@angular/core';
import { ProductsService } from 'src/app/service/products.service';
import { FormsModule } from '@angular/forms';

@Component({
  selector: 'app-admin',
  templateUrl: './admin.component.html',
  styleUrls: ['./admin.component.css']
})
export class AdminComponent implements OnInit {
  constructor(private proSrv: ProductsService) { }
  carts: any[] = [];
  products: any;
ngOnInit(): void {
  this.getCarts();
  this.getProducts();
}
  getCarts() {
    this.proSrv.getAllCarts().subscribe((res:any) => {
      this.carts = res;
      console.log(this.carts)
    })
  }

  getProducts() {
    this.proSrv.getallproducts().subscribe((res: any) => {
      this.products = res;
      console.log(this.products)
    })
  }
  deletP(index: any) {
    this.products.splice(index, 1);
  }
}
<div *ngFor="let pro of products,let index=index" class="mx-3" style="display: flex; align-items: center; width: 300px;">
    <img src="{{pro.image}}" style="width: 200px;" class="m-4" alt="">
    <p class="mx-3">{{pro.title}}</p>
    <p>{{pro.price}}</p>
    <button class="btn btn-danger mx-5 px-5" (click)="deletP(index)">delet</button>
    <button routerLink="/admin/product/edit/{{pro.id}}" class="btn btn-success mx-2 px-5">edit</button>
</div>
