import { Component, OnInit } from '@angular/core';
import { ActivatedRoute,Route } from '@angular/router';
import { ProductsService } from 'src/app/service/products.service';
import { FormsModule } from '@angular/forms';

@Component({
  selector: 'app-edit',
  templateUrl: './edit.component.html',
  styleUrls: ['./edit.component.css']
})
export class EditComponent implements OnInit{
  id: any;
  product: any = {};
  constructor(private actRout: ActivatedRoute, private proServe: ProductsService,router:Route) { 
    this.id = this.actRout.snapshot.paramMap.get('id');
  }

  ngOnInit(): void {
    this.getPro();
  }
  getPro(){
    this.proServe.getSigleProduct(this.id).subscribe((res: any) => {
      this.product=res
    })
    console.log(this.product)
  }

  update() {
    this.proServe.updateProduct(this.product, this.id).subscribe((res) => {
      this.actRout
    })
  }
}
