import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { ProductsService } from 'src/app/service/products.service';

@Component({
  selector: 'app-details',
  templateUrl: './details.component.html',
  styleUrls: ['./details.component.css']
})
export class DetailsComponent implements OnInit{
  id: any;
  product: any = {};
  quantity: number = 0;
  constructor(private activeRout:ActivatedRoute,private service:ProductsService) {
    this.id = this.activeRout.snapshot.paramMap.get('id')
  }
  ngOnInit(): void {
      this.getProduct()
  }
  getProduct() {
    this.service.getSigleProduct(this.id).subscribe((pro: any) => {
      this.product = pro
    })
  }

  addAmount
}
<div class="mx-4 my-5" style="width: 300px;">
    <p>{{product.title}}</p>
    <img src="{{product.image}}" style="width: 200px;" alt="">
    <p>{{product.description}}</p>
    <div class="quantity">
        <button class="btn btn-dark" (click)="addAmount()">+</button>
        <input type="text" id="" [(ngModel)]="quantity">
        <button class="btn btn-dark" (click)="minAmount()">-</button>
    </div>
    <p style="margin-top: 5px;">{{product.price}} LE</p>
    <div class="" style="display:flex;justify-content:space-between">
        <button class="btn btn-dark px-4">add to cart</button>
        <button  class="btn btn-success  px-4">buy now</button>
    </div>
</div>
